# compar reaanage and compare dates# returns 0 if in the range #         -1 if below the lower bound, #         1 if above the upper bound# a - max date range# b - min date range# c - input date value# inputs date format - mmddyyyy compar(){    echo "$1 $2 $3" |\ awk 'function rearrange( mySTRING ){        return  substr(mySTRING,5) substr(mySTRING,1,2) substr(mySTRING,3,2)      }      { one=rearrange($1)        two=rearrange($2)        three=rearrange($3)        if(three > one) {print 1;exit}        if(three < two) { print -1;exit}        print 0      }    '}#a=01312009#b=12012008#c=01012008#echo "$1 $2 $3"if [[  $(compar "$1" "$2" "$3") -eq 0 ]];  then # echo "$3 is in the range between $1 and $2"      exit 0else# echo "not in date range" exit 1fi
